import React, { Component } from 'react';
import './App.css';
import axios from 'axios';



class App extends Component {
    // constructor(props){
    //   super(props);
    //   this.state={
    //     products:[],
    //     cart: []

    //   }
    // }

    state = {
      products: [],
      cart: []
    }

    componentDidMount(){
      axios.get("http://localhost:3000/products")
      .then(res => {
        const products = res.data

          this.setState({
            products
          });      
        })

        .catch((error)=>{
          alert("There is an error in API call.");
          console.log(error)
        });
    }

    addToCart = () => {
      this.setState(prevState => ({
        cart: [...prevState.cart, product] }))

        return {
          cart
        }
      }



      render(){ 

        // if(this.state.products) {
          // const loadData = () => JSON.parse(JSON.stringify(this.state.products));
          // console.log(this.state.products)
          return (
            <div>
              <header>
                <p> Items Available Now! </p>
                <h1 className="App-title">Welcome to the Hacky Mess shop that only has 4 items! </h1>
              </header>


              <span>

              { this.state.products.map(product => 
                // <ul key={product.uuid.toString()}
                // value={product}>

        <ul>
                        <div >

                        <div >
                        <h1 className="name">{product.name}</h1>
                        <p className="uuid">{product.uuid}</p>

                        <p className="price">Price: ${product.price}</p>
                        <button type="button" onClick={this.addToCart.bind(product)}>
                          Add to Cart
                        </button>
                      </div>
                        </div>



                      </ul>
               ) }




              </span>

             </div> 
              );
            }
      //       {
      // return <div>Loading items</div>

      // }}
    }



export default App;
