    constructor(props){
      super(props);
      this.state={
        products:[],
        cart: []

      }
    }

    // state = {
    //   products: [],
    //   cart: []
    // }

    componentDidMount(){
      axios.get("http://localhost:3000/products")
      .then(res => {
        const products = res.data
          this.setState({
            products
          });      
        })

        .catch((error)=>{
          alert("There is an error in API call.");
          console.log(error)
        });
    }

    // addToCart = (product) => {
