import React, { Component } from 'react';
import './App.css';
import axios from 'axios';



class App extends Component {
    constructor(props){
      super(props);
      this.state={
        products:[],
        cart: []
      }
    }

    // state = {
    //   products: [],
    //   cart: []
    // }

    componentDidMount(){
      axios.get("http://localhost:3000/products")
      .then(res => {
        const products = res.data

          this.setState({
            products
          });      
        })

        .catch((error)=>{
          alert("There is an error in API call.");
          console.log(error)
        });
    }

    addToCart = product => {
      let { cartItems } = this.state;
      const alreadyExists = cartItems.some(
        item => item.product.id === product.id
      );

      if (alreadyExists) {
        cartItems = cartItems.map(item => {
          if (item.product.id === product.id) {
            item.quantity = item.quantity + 1;
          }
          return item;
        });
      } else {
        cartItems.push({
          product,
          quantity: 1
        });
      }

      this.setState({ cartItems });
    };






    // addToCart = (props) => {
    //   // const id = this.props.uuid
    //   const cart = this.state.cart.concat(this.props.value);
    //   // cart.push(product);

    //   this.setState({

    //     cart,
    //   });
    // }


      // addToCart = () => {
      //   this.setState(state => {
      //     const cart = state.cart.concat()
      //     return {
      //       cart
      //     }
      //   })
      // }

    // addToCart = () => {
    //   this.setState(state => {
    //     const cart = state.cart.concat([...this.state.cart])
    //     return {
    //       cart
    //     }
    //   }) 
    //   }



      render(){ 

        // if(this.state.products) {
          // const loadData = () => JSON.parse(JSON.stringify(this.state.products));
          // console.log(this.state.products)
          return (
            <div>
              <header>
                <p> <span > Items Available Now!  </span> </p>
                <h1 className="App-title">Welcome to the Hacky Mess shop that only has 4 items! </h1>
              </header>


              <span>

              { this.state.products.map(product => 
                <ul key={product.uuid.toString()}
                value={product}>


                        <div >

                        <div >
                        <h1 className="name">{product.name}</h1>
                        <p className="uuid">{product.uuid}</p>

                        <p className="price">Price: ${product.price}</p>
                        <button type="button" onClick={this.addToCart(product)}>
                          Add to Cart
                        </button>
                      </div>
                        </div>



                      </ul>
               ) }




              </span>

             </div> 
              );
            }
      //       {
      // return <div>Loading items</div>

      // }}
    }



export default App;
