                .catch((error)=>{
                  //on error
                  alert("There is an error in API call.");
                  console.log(error)
                });
    }


      handleClick(e) {
        e.preventDefault();
        console.log("The link was clicked");


      }

      addToCart = (props) => {
        this.setState(state => {
          const cart = state.cart.concat(this.props)
          return {
            cart
          }
        }) 
        }


              render(){ 

        if(this.state.products) {
          // const loadData = () => JSON.parse(JSON.stringify(this.state.products));
          // console.log(this.state.products)
          return (
            <div className="App">
              <header className="App-header">
                <p> <span role="img" aria-label="product">Items Available Now!  </span> </p>
                <h1 className="App-title">Welcome to the Hacky Mess shop that only has 4 items! </h1>
              </header>


              <span>

              { this.state.products.map(product => 
                <ul>

                      <li key={product}>
                        <div >

                          <div >
                            <h1 className="name">{product.name}</h1>
                            <p className="uuid">{product.uuid}</p>

                            <p className="price">Price: ${product.price}</p>
                            <button type="button" onClick={this.addToCart.bind(product.uuid)} >
                              Add to Cart
                            </button>
                          </div>
                        </div>
                      </li>


                      </ul>
               ) }


              </span>

              </div>
              );
            }

            return <div>Loading items</div>
          }


