import React, { Component } from 'react';
import './App.css';
import axios from 'axios';



class App extends Component {
    // constructor(){
    //   super();
    //   this.state={
    //     products:[],
    //     cart: []
    //   }
    // }

    state = {
      products: [],
      cart: []
    }

    componentDidMount(){
      axios.get("http://localhost:3000/products")
      .then(res => {
        const products = res.data

          this.setState({
            products
          });      
        })

        .catch((error)=>{
          alert("There is an error in API call.");
          console.log(error)
        });
    }





      addToCart = () => {
        this.setState(state => {
          const cart = state.cart.concat(this.state)
          return {
            cart
          }
        }) 
        }


      render(){ 

        if(this.state.products) {
          // const loadData = () => JSON.parse(JSON.stringify(this.state.products));
          // console.log(this.state.products)
          return (
            <div>
              <header>
                <p> <span > Items Available Now!  </span> </p>
                <h1 className="App-title">Welcome to the Hacky Mess shop that only has 4 items! </h1>
              </header>


              <span>

              { this.state.products.map(product => 
                <ul>


                        <div >

                          <div key={product.id}>
                            <h1 className="name">{product.name}</h1>
                            <p className="uuid">{product.uuid}</p>

                            <p className="price">Price: ${product.price}</p>
                            <button type="button" onClick={this.addToCart(product)} >
                              Add to Cart
                            </button>
                          </div>
                        </div>



                      </ul>
               ) }

               <div>
               {}
               </div>


              </span>

             </div> 
              );
            }
            {
      return <div>Loading items</div>

      }}
    }



export default App;
