import React, { Component } from 'react';
import './App.css';
import axios from 'axios';


class App extends Component {
    constructor(){
      super();
      this.state={
        products:[]
      }
    }
    componentDidMount(){
      axios.get("http://localhost:3000/products")
      .then((res)=>{
          //on success
          this.setState({
        products:res.data
      });

      }).catch((error)=>{
        //on error
        alert("There is an error in API call.");
        console.log(error)
      });
    }

  render(){
    return(

          this.state.products!=null &&
          <div>
            <h2>{this.state.products.name}</h2>
            <p>{this.state.products.price}</p>
            <p>{this.state.products}</p>
          </div>

      );
    }
  }  

  export default App;




